    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        // Delete Item by ID
        if(Frame1.shop.getNumOfDesserts() == 0) {
            JOptionPane.showMessageDialog(this, "The Shop is Empty !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
            return;
	}
        
        if(jTextField1.getText().equals("")){
             JOptionPane.showMessageDialog(this, "Please Enter the ID first!" , "WARNING" , JOptionPane.WARNING_MESSAGE);
            return;
        }
        
        
        String idStr = jTextField1.getText();
        
        int id;
        
        try{
            
            id = Integer.parseInt(idStr);
            
            if(id < 0)
                throw new NegativeNumberException();
            
            
        }catch(NumberFormatException e){
             JOptionPane.showMessageDialog(this, "Please Enter an Integer Number !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
             jTextField1.setText("");
             return;
        }catch(NegativeNumberException e){
             JOptionPane.showMessageDialog(this, "Please Enter a Positive Number !", "WARNING" , JOptionPane.WARNING_MESSAGE);
                 jTextField1.setText("");
                 return;
        }
        
        
        if(Frame1.shop.DeleteDessert(id)){
            JOptionPane.showMessageDialog(null, "Dessert was Deleted Succssefuly");
            jTextField1.setText("");
        }
        else{
            JOptionPane.showMessageDialog(null, "Either Item's ID is not Found\nOR item's ID is incorrect" , "ALERT" , JOptionPane.WARNING_MESSAGE);
            jTextField1.setText("");
        }

        
        
        
    }//GEN-LAST:event_jButton2ActionPerformed

