    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // Add Cake
        
        if(jTextField2.getText().equals("") || jTextField3.getText().equals("") || jTextField5.getText().equals("") || jTextField6.getText().equals("")){
            JOptionPane.showMessageDialog(this, "Please Complete the Informations !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
            return;
        }
        
        int id ;
        try{
        String idStr = jTextField2.getText();
        id = Integer.parseInt(idStr);
        
        if(id < 0)
            throw new NegativeNumberException();
        
        }catch(NumberFormatException e){
             JOptionPane.showMessageDialog(this, "Please Enter an Integer Number !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
             jTextField2.setText("");
             return;
        }catch(NegativeNumberException e){
             JOptionPane.showMessageDialog(this, "Please Enter a Positive Number !", "WARNING" , JOptionPane.WARNING_MESSAGE);
                 jTextField2.setText("");
                 return;
        }
        
        String flavor = jTextField3.getText();
        
       
        char size = ' ';
        if(jRadioButton1.isSelected())
            size = 'S';
        else if(jRadioButton3.isSelected())
            size = 'M';
        else if(jRadioButton2.isSelected())
            size = 'L';
        
        
        int quantity;
        try{
        String quantStr = jTextField6.getText();
        quantity = Integer.parseInt(quantStr);
        
        if(quantity < 0)
            throw new NegativeNumberException();
        }catch(NumberFormatException e){
                JOptionPane.showMessageDialog(this, "Please Enter an Integer Number !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
                jTextField6.setText("");
                return;
        }
        catch(NegativeNumberException e){
                 JOptionPane.showMessageDialog(this, "Please Enter a Positive Number !", "WARNING" , JOptionPane.WARNING_MESSAGE);
                 jTextField6.setText("");
                 return;
        }
        
         if(!(jRadioButton1.isSelected() || jRadioButton3.isSelected() || jRadioButton2.isSelected())){
            
             JOptionPane.showMessageDialog(this, "Please Choose a Cake Size !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
            return;
        }
         
        String layerStr = jTextField5.getText();
        int numL;
        try{
            numL = Integer.parseInt(layerStr);
            
            if(numL < 0)
                throw new NegativeNumberException();
            
        }catch(NumberFormatException e){
             JOptionPane.showMessageDialog(this, "Please Enter an Integer Number !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
             jTextField5.setText("");
             return;
        }catch(NegativeNumberException e){
             JOptionPane.showMessageDialog(this, "Please Enter a Positive Number !", "WARNING" , JOptionPane.WARNING_MESSAGE);
                 jTextField5.setText("");
                 return;
        }
         
        
        Desserts cake = new Cake(id , flavor , size , numL , quantity);
        Frame1.shop.AddDessert(cake);
        
        jTextField2.setText("");
        jTextField3.setText("");
        buttonGroup1.clearSelection();
        jTextField6.setText("");
        jTextField5.setText("");
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // Add Cookie
        
        if(jTextField7.getText().equals("") || jTextField8.getText().equals("") || jTextField10.getText().equals("")){
            JOptionPane.showMessageDialog(this, "Please Complete the Informations !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
            return;
        }
        
        int id ;
        try{
        String idStr = jTextField7.getText();
        id = Integer.parseInt(idStr);
        
        if(id < 0)
            throw new NegativeNumberException();
        
        }catch(NumberFormatException e){
             JOptionPane.showMessageDialog(this, "Please Enter an Integer Number !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
             jTextField7.setText("");
             return;
        }catch(NegativeNumberException e){
             JOptionPane.showMessageDialog(this, "Please Enter a Positive Number !", "WARNING" , JOptionPane.WARNING_MESSAGE);
                 jTextField7.setText("");
                 return;
        }
        
        String flavor = jTextField8.getText();
        
       
        char size = ' ';
        if(jRadioButton4.isSelected())
            size = 'S';
        else if(jRadioButton5.isSelected())
            size = 'L';
        
        
        int quantity;
        try{
        String quantStr = jTextField10.getText();
        quantity = Integer.parseInt(quantStr);
        
        if(quantity < 0)
            throw new NegativeNumberException();
        }catch(NumberFormatException e){
                JOptionPane.showMessageDialog(this, "Please Enter an Integer Number !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
                jTextField10.setText("");
                return;
        }
        catch(NegativeNumberException e){
                 JOptionPane.showMessageDialog(this, "Please Enter a Positive Number !", "WARNING" , JOptionPane.WARNING_MESSAGE);
                 jTextField10.setText("");
                 return;
        }
        
         if(!(jRadioButton4.isSelected() || jRadioButton5.isSelected())){
            
             JOptionPane.showMessageDialog(this, "Please Choose a Cookie Size !" , "WARNING" , JOptionPane.WARNING_MESSAGE);
            return;
        }
         
         boolean withC;
         if(jCheckBox1.isSelected())
             withC = true;
         else
             withC = false;
         
        
        Desserts cookie = new Cookie(id , flavor , size , withC , quantity);
        Frame1.shop.AddDessert(cookie);
        
        jTextField7.setText("");
        jTextField8.setText("");
        buttonGroup2.clearSelection();
        jTextField10.setText("");
        jCheckBox1.setSelected(false);
    }//GEN-LAST:event_jButton4ActionPerformed

